import { useDispatch, useSelector } from 'react-redux';
import { toggleSidebar } from 'ra-core';
/**
 * A hook that returns the sidebar open state and a function to toggle it.
 * @returns A tuple containing a boolean indicating whether the sidebar is open or not and a function to toggle the sidebar.
 * @example
 * const MyButton = () => {
 *     const [sidebarOpen, toggleSidebar] = useToggleSidebar();
 *     return (
 *         <Button
 *             color="inherit"
 *             onClick={() => toggleSidebar()}
 *         >
 *             {open ? 'Open' : 'Close'}
 *         </Button>
 *     );
 */
export var useToggleSidebar = function () {
    var open = useSelector(function (state) { return state.admin.ui.sidebarOpen; });
    var dispatch = useDispatch();
    var toggle = function () { return dispatch(toggleSidebar()); };
    return [open, toggle];
};
